package com.testing.class3;

public class ExamplesforLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//求能够被7整除的100以内的数
		for(int i=1;i<=100;i++) {
			//判断是否能被7整除
			if(i%7==0) {
				//println输出后换行
//				System.out.println(i);
				//print不换行输出
				System.out.print(i+" ");
			}
		}
		

		
		
	}

}
